import { useState, useEffect } from "react";
import axios from "axios";

const App = () => {
  const [countries, setContries] = useState([]);
  const [allContries, setAllContries] = useState([]);
  const [searchCountry, setsearchCountry] = useState("");
  useEffect(() => {
    axios.get("https://restcountries.com/v3.1/all").then(({ data }) => {
      console.log(data);
      setAllContries(data);
    });
  }, []);

  const handleChange = (e) => {
    const value = e.target.value;
    setsearchCountry(value);
    const find_countries = allContries.filter(({ name: { common } }) => {
      return common.toLowerCase().includes(value.toLowerCase());
    });

    setContries(find_countries);
  };
  return (
    <>
      <div>
        find countries
        <input onChange={handleChange} value={searchCountry} />
      </div>
      <Countries countries={countries}></Countries>
    </>
  );
};

const Countries = ({ countries }) => {
  if(countries.length == 1) {

      return <>
        <h1></h1>
        <p>capital Bern</p>
        <p>capital Bern</p>
      </>
  }
  if (countries.length > 10) return <p> too many countries</p>;
  return countries.map(({ name: { common } }) => (
    <Country key={common} name={common}></Country>
  ));
};
const Country = ({ name }) => {
  return <p>{name}</p>;
};

export default App;
